CREATE OR REPLACE PROCEDURE moises.iudp_persona_telefono ( P_Modo text, P_ID_PERSONA integer, P_ID_TELEFONO integer, P_ID_TIPOTELEFONO integer DEFAULT NULL, P_NUM_TELEFONO text DEFAULT NULL, P_ES_ACTIVO integer DEFAULT NULL, P_ES_PRIVADO integer DEFAULT NULL, P_COMENTARIO text DEFAULT NULL) AS $body$
DECLARE

  S_bExiste varchar(1) := '0';
  S_ID_TELEFONO integer;
  S_NUM_TELEFONO varchar(100);

BEGIN

  
  IF P_Modo = 0 THEN
    S_NUM_TELEFONO := trim(both coalesce(P_NUM_TELEFONO, ''));
    S_ID_TELEFONO := trim(both coalesce(P_ID_TELEFONO, ''));


    IF not(S_NUM_TELEFONO is null) THEN
      IF S_ID_TELEFONO = 0 THEN
        SELECT 
          coalesce(max(ID_TELEFONO),0) INTO STRICT S_ID_TELEFONO
        FROM Persona_TELEFONO
        WHERE ID_PERSONA = P_ID_PERSONA
          AND ID_TIPOTELEFONO = P_ID_TIPOTELEFONO
          AND NUM_TELEFONO = S_NUM_TELEFONO;

        IF S_ID_TELEFONO = 0 THEN
          SELECT
            MAX(ID_TELEFONO) INTO STRICT S_ID_TELEFONO
          FROM Persona_TELEFONO
          WHERE ID_PERSONA = P_ID_PERSONA;

          S_ID_TELEFONO := coalesce(S_ID_TELEFONO, 0) + 1;
          RAISE NOTICE '%', P_ID_PERSONA;
          RAISE NOTICE '%', P_ID_PERSONA;

          INSERT INTO Persona_TELEFONO(ID_TELEFONO, ID_PERSONA, ID_TIPOTELEFONO, NUM_TELEFONO, ES_ACTIVO, ES_PRIVADO, COMENTARIO)
            VALUES (S_ID_TELEFONO, P_ID_PERSONA, P_ID_TIPOTELEFONO, S_NUM_TELEFONO, P_ES_ACTIVO, P_ES_PRIVADO, P_COMENTARIO);
        ELSE
          S_bExiste := '1';
        END IF;

      ELSE 
        S_bExiste := '1';
      END IF;

      IF S_bExiste = 1 THEN
        UPDATE Persona_TELEFONO SET
          COMENTARIO = P_COMENTARIO
        WHERE ID_PERSONA = P_ID_PERSONA
        AND ID_TELEFONO = S_ID_TELEFONO;
      END IF;
    END IF;

  ELSE
    DELETE FROM Persona_TELEFONO WHERE ID_PERSONA = P_ID_PERSONA AND ID_TELEFONO = S_ID_TELEFONO;
  END IF;

END;
$body$
LANGUAGE PLPGSQL
;
