CREATE OR REPLACE FUNCTION caleb.ft_beca_id (p_documento text, p_id_convocatoria_det text) RETURNS varchar AS $body$
DECLARE


  res varchar(100);




BEGIN  

	

	SELECT coalesce(id,0) INTO STRICT res FROM (


	SELECT  tbe.id_tipo_beca_estatal AS id FROM 



		caleb.ADM_BECADO_CONVOCATORIA abc 
		INNER JOIN david.TIPO_BECA_ESTATAL tbe ON tbe.ID_TIPO_BECA_ESTATAL = abc.id_tipo_beca
		

		WHERE abc.numero_documento=p_documento

		AND id_convocatoria_detalle=p_id_convocatoria_det



	) alias1 LIMIT 1;



	

  return res;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
