CREATE OR REPLACE FUNCTION caleb.ft_celular (p_id_persona text) RETURNS varchar AS $body$
DECLARE


  res varchar(50);




BEGIN  

	

	SELECT numero 

	INTO STRICT res 

	FROM (


	SELECT coalesce(num_telefono, 'Sin Celular') AS numero FROM 
	genesis.PERSONA_TELEFONO pt 
	WHERE ES_ACTIVO ='1'
	) alias1 LIMIT 1;



	

  return res;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
