CREATE OR REPLACE FUNCTION caleb.ft_correo_moises (p_id_persona text) RETURNS varchar AS $body$
DECLARE


  res varchar(50);


BEGIN

	SELECT direccion 



	INTO STRICT res 


	FROM (


	SELECT coalesce(direccion, 'Sin correo') AS direccion FROM 
	moises.PERSONA_VIRTUAL pv 
	INNER JOIN genesis.persona pe ON pe.ID_PERSONA_LAMB =pv.ID_PERSONA  
	WHERE pv.ES_ACTIVO ='1' AND pe.ID_PERSONA = p_id_persona

	) alias1 LIMIT 1;



  return res;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
