CREATE OR REPLACE FUNCTION caleb.ft_tipo_documento (p_id_persona text) RETURNS varchar AS $body$
DECLARE


  res varchar(50);


BEGIN

 	SELECT documento 
 
 	INTO STRICT res 
 
 	FROM (
 
 	SELECT coalesce(pd.ID_TIPODOCUMENTO, 0) AS documento FROM 
 
   	genesis.PERSONA_DOCUMENTO pd 
 
 	WHERE ES_ACTIVO ='1' AND ID_PERSONA =p_id_persona
 
 	ORDER BY pd.ID_TIPODOCUMENTO 
  
 	) alias1 LIMIT 1;

  return res;

end;
$body$
LANGUAGE PLPGSQL
