function          FT_CELULAR_MOISES(p_id_persona in VARCHAR2) 

 
return VARCHAR2
 
is

  res VARCHAR2(50);
 
begin  

 
	SELECT numero 

	INTO res 

	FROM (
 
	SELECT  num_telefono  AS numero FROM 

	moises.PERSONA_TELEFONO pt 

	INNER JOIN genesis.persona pe ON pe.ID_PERSONA_LAMB = pt.ID_PERSONA 

	WHERE ES_ACTIVO ='1'

	AND pe.ID_PERSONA =p_id_persona

	)

	WHERE rownum=1

	;

 

  return res;
 
end;   
   

