function             FT_CELULAR(p_id_persona in VARCHAR2) 

return VARCHAR2

is

  res VARCHAR2(50);

  

begin  

	

	SELECT numero 

	INTO res 

	FROM (


	SELECT nvl(num_telefono, 'Sin Celular') AS numero FROM 
	genesis.PERSONA_TELEFONO pt 
	WHERE ES_ACTIVO ='1'
	)

	WHERE rownum=1

	;



	

  return res;

  

end;

