function             FT_CORREO_MOISES(p_id_persona in VARCHAR2) 

return VARCHAR2



is

  res VARCHAR2(50);

begin  

	SELECT direccion 



	INTO res 


	FROM (


	SELECT nvl(direccion, 'Sin correo') AS direccion FROM 
	moises.PERSONA_VIRTUAL pv 
	INNER JOIN genesis.persona pe ON pe.ID_PERSONA_LAMB =pv.ID_PERSONA  
	WHERE pv.ES_ACTIVO ='1' AND pe.ID_PERSONA = p_id_persona

	)



	WHERE rownum=1

	;



  return res;



end;

