SET client_encoding TO 'UTF8';

SET search_path = caleb,public;
\set ON_ERROR_STOP ON

function       FT_CORREO(p_id_persona in VARCHAR2) 

return VARCHAR2

is

  res VARCHAR2(50);

  

begin  

	

	SELECT direccion 

	INTO res 

	FROM (


	SELECT nvl(direccion, 'Sin correo') AS direccion FROM 
 	genesis.PERSONA_VIRTUAL pv   
	WHERE ES_ACTIVO ='1' AND ID_PERSONA = p_id_persona
	)

	WHERE rownum=1

	;



	

  return res;

  

end;

