SET client_encoding TO 'UTF8';

SET search_path = caleb,public;
\set ON_ERROR_STOP ON

function             ft_documento(p_id_persona in VARCHAR2) 

return VARCHAR2

is

  res VARCHAR2(50);

  

begin  

 
	SELECT documento 

	INTO res 

	FROM (


	SELECT nvl(num_documento, 'Sin correo') AS documento FROM 
  	genesis.PERSONA_DOCUMENTO pd 
	WHERE ES_ACTIVO ='1' AND ID_PERSONA =p_id_persona
	ORDER BY pd.ID_TIPODOCUMENTO 
	)

	WHERE rownum=1

	;



	

  return res;

  

end;

