SET client_encoding TO 'UTF8';

SET search_path = caleb,public;
\set ON_ERROR_STOP ON

function             ft_paso(p_id_persona_inscripcion in integer) 
return varchar2
is
  p_paso varchar2(200);
begin  

     select 
        nvl((
    select t.paso from (
    select cdc.id_parent, cdc.orden , cdc.nombre as paso , cdc.id_conv_det_config as id_paso 
    from CALEB.adm_persona_inscripcion pi 
    inner join CALEB.adm_postulante_paso pp on pi.id_persona_inscripcion = pp.id_persona_inscripcion 
    inner join CALEB.adm_conv_det_config cdc on pp.id_conv_det_config = cdc.id_conv_det_config
    where 
     pi.id_persona_inscripcion = p_id_persona_inscripcion
    order by 1, 2 desc ) t
    where rownum=1 ), 'Paso inicial') into p_paso 
    from dual ;

  return p_paso;

end;

