CREATE OR REPLACE FUNCTION david.dia_hora_curso3 (HORARIO text,v_id_horario integer) RETURNS varchar AS $body$
DECLARE


dias varchar(100) := ' ';
dias2 varchar(100);
corte integer :=1;
turno2 varchar(100);
turno varchar(100);
val varchar(100);
var varchar(100);

tm integer;
tt integer;
tn integer;
BEGIN

select sum(CASE WHEN id_tipo_turno=1 THEN 1  ELSE 0 END ),
sum(CASE WHEN id_tipo_turno=1 THEN 1  ELSE 0 END )+sum(CASE WHEN id_tipo_turno=2 THEN 1  ELSE 0 END ),
sum(CASE WHEN id_tipo_turno=1 THEN 1  ELSE 0 END )+sum(CASE WHEN id_tipo_turno=2 THEN 1  ELSE 0 END )+sum(CASE WHEN id_tipo_turno=3 THEN 1  ELSE 0 END )
into STRICT tm,tt,tn
from ACAD_HORARIO_DETALLE where id_horario=v_id_horario
group by id_horario;

  FOR i IN 1..30 LOOP
      val := SUBSTR(HORARIO,corte ,corte+7);
     FOR y IN 1..7 LOOP
      var := substr(val,y,1);
      IF var ='1' THEN
        IF position(y::text in dias) =0 THEN
          dias :=CONCAT(dias, y);
          CASE
             WHEN i<=tm THEN turno :=CONCAT(turno, CONCAT('M',i));
             WHEN i>tm and i<=tt THEN turno :=CONCAT(turno,CONCAT('T',i-tm));
             ELSE
              turno :=CONCAT(turno,CONCAT('N',i-tt));
          END CASE;
          --turno :=CONCAT(turno,turno);
        END IF;
      END IF;

    END LOOP;
      corte:=corte+7;
  END LOOP;

  corte:=1;
  IF LENGTH(trim(both dias)) >0 THEN
    FOR y IN 1..length(trim(both dias)) LOOP
      var := substr(trim(both dias),y,1);
      turno2 := substr(trim(both turno),corte,2);
      corte :=corte+2;
      CASE
       WHEN var = '1' THEN IF y<length(trim(both dias)) THEN dias2 :=CONCAT(dias2, CONCAT('D', CONCAT(turno2,',')));ELSE dias2 :=CONCAT(dias2, CONCAT('D',turno2));END IF;
       WHEN var = '2' THEN IF y<length(trim(both dias)) THEN dias2 :=CONCAT(dias2, CONCAT('L',CONCAT(turno2,',')));ELSE dias2 :=CONCAT(dias2, CONCAT('L', turno2));END IF;
       WHEN var = '3' THEN IF y<length(trim(both dias)) THEN dias2 :=CONCAT(dias2, CONCAT('R',CONCAT(turno2,',')));ELSE dias2 :=CONCAT(dias2, CONCAT('R',turno2));END IF;
       WHEN var = '4' THEN IF y<length(trim(both dias)) THEN dias2 :=CONCAT(dias2, CONCAT('W',CONCAT(turno2,',')));ELSE dias2 :=CONCAT(dias2, CONCAT('W',turno2));END IF;
       WHEN var = '5' THEN IF y<length(trim(both dias)) THEN dias2 :=CONCAT(dias2, CONCAT('J',CONCAT(turno2,',')));ELSE dias2 :=CONCAT(dias2, CONCAT('J', turno2));END IF;
       WHEN var = '6' THEN IF y<length(trim(both dias)) THEN dias2 :=CONCAT(dias2, CONCAT('V',CONCAT(turno2,',')));ELSE dias2 :=CONCAT(dias2, CONCAT('V',turno2));END IF;
       ELSE IF y<=length(trim(both dias)) THEN dias2 :=CONCAT(dias2, CONCAT('S',CONCAT(turno2,',')));ELSE dias2 :=CONCAT(dias2, CONCAT('S', turno2));END IF;
      END CASE;
    END LOOP;
  END IF;


RETURN(trim(both dias2));
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
