CREATE OR REPLACE FUNCTION david.ft_acad_alum_ciclo ( V_ID_PERSONA bigint , V_ID_PLAN_PROGRAMA bigint ) RETURNS varchar AS $body$
DECLARE

p_semestre varchar(500);
p_ciclo varchar(500);

BEGIN
    SELECT MAX(ASM.SEMESTRE) SEMESTRE into STRICT p_semestre
    FROM ACAD_ALUMNO_CONTRATO AAC INNER JOIN ACAD_SEMESTRE_PROGRAMA ASP ON ASP.ID_SEMESTRE_PROGRAMA=AAC.ID_SEMESTRE_PROGRAMA
                                AND AAC.ID_PERSONA= V_ID_PERSONA
                                            and AAC.ID_PLAN_PROGRAMA=V_ID_PLAN_PROGRAMA
                                            AND AAC.ESTADO='1'
    INNER JOIN ACAD_SEMESTRE ASM ON ASM.ID_SEMESTRE=ASP.ID_SEMESTRE WHERE SUBSTR(ASM.SEMESTRE,-2) IN ('-1','-2');
    IF (p_semestre IS NOT NULL AND p_semestre::text <> '') THEN
        SELECT coalesce(ft_calcular_ciclo_programa(p_semestre,V_ID_PERSONA,V_ID_PLAN_PROGRAMA),0)+1 AS CICLO into STRICT p_ciclo;
    ELSE
       p_ciclo := null;
    END IF;
  RETURN p_ciclo;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
