CREATE OR REPLACE FUNCTION david.ft_acad_fecha_silabo ( V_ID_CARGA_CURSO bigint ) RETURNS varchar AS $body$
DECLARE


p_fecha varchar(500);

BEGIN

select min(Amd.Fecha_Inicio)::text||' - '|| max(Amd.Fecha_Fin)::text into STRICT p_fecha
from david.acad_carga_curso_docente accd inner join acad_modulo_detalle amd on Amd.Id_Modulo_Detalle=Accd.Id_Modulo_Detalle
inner join acad_carga_curso acc on acc.id_carga_curso=accd.id_carga_curso and acc.id_carga_curso= V_ID_CARGA_CURSO;

  RETURN p_fecha;
END;
