CREATE OR REPLACE FUNCTION david.ft_alumno_irregular (p_id_persona integer,p_id_plan_programa integer,p_id_semestre integer) RETURNS varchar AS $body$
DECLARE

  v_res varchar(20);
  v_ciclo bigint;
  v_max_ciclo bigint;

BEGIN

  select max(CASE WHEN substr(aac.semestre,-1)=2 THEN (substr(aac.semestre,0,4)-substr(substr(ap.Nombre,-6),0,4)+1)*2  ELSE ((substr(aac.semestre,0,4)-substr(substr(Ap.Nombre,-6),0,4)+1)*2)-1 END ) into STRICT v_ciclo
  from Vw_Acad_Alumno_Contrato aac inner join Acad_Plan_Programa app on App.Id_Plan_Programa=Aac.Id_Plan_Programa
  inner join acad_plan ap on ap.id_plan=app.id_plan
  where aac.id_persona=p_id_persona and aac.id_semestre=p_id_semestre and aac.id_plan_programa=p_id_plan_programa;

  select max(ciclo) into STRICT v_max_ciclo
  from Acad_Plan_Programa app inner join acad_plan ap on ap.id_plan=app.id_plan
  inner join acad_plan_curso apc on Apc.Id_Plan=ap.id_plan
  where app.id_plan_programa=p_id_plan_programa;

  if (v_ciclo>v_max_ciclo or v_ciclo=0) then
  
    v_res:='S';

  else
  
    select CASE WHEN(select count(*)                    from acad_plan_programa app inner join vw_acad_curso_plan acpe on Acpe.Id_Plan=app.id_plan                    where app.id_plan_programa=p_id_plan_programa and acpe.ciclo=v_ciclo and coalesce(Acpe.Id_Plan_Curso_Electivo::text, '') = ''                    and not Exists (select 1                                    from acad_curso_alumno aca inner join acad_plan_curso acp on Acp.Id_Plan_Curso=Aca.Id_Plan_Curso                                    where aca.id_persona=p_id_persona and aca.Id_Plan_Programa=app.id_plan_programa and acp.ciclo=acpe.ciclo and Aca.Id_Tipo_Condicion in (1,13,17,7)                                    and CASE WHEN coalesce(Acp.Id_Plan_Curso_Electivo::text, '') = '' THEN Acp.Id_Plan_Curso  ELSE Acp.Id_Plan_Curso_Electivo END =Acpe.Id_Plan_Curso ) )=0 THEN 'N'  ELSE 'S' END  into STRICT v_res
;

  end if;

  return v_res;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
