CREATE OR REPLACE FUNCTION david.ft_al_semestre_ant ( v_id_persona integer,v_id_escuela integer,v_semestre text,v_tipo text) RETURNS varchar AS $body$
DECLARE

	p_semestre	  varchar(800);
		

BEGIN

	select semestre into STRICT p_semestre
  from acad_semestre where id_semestre=v_semestre;

  select max(Semestre) into STRICT p_semestre 
  from Vw_Acad_Alumno_Contrato 
  where id_persona=v_id_persona and Id_Escuela=v_id_escuela
  and Id_semestre<>v_semestre and estado='1' and semestre<p_semestre
  and substr(semestre,-2) in ('-1','-2','-3') and id_modo_contrato=1;

  if (v_tipo='ID') then
  
  select ''||id_semestre into STRICT p_semestre
  from acad_semestre 
  where semestre=p_semestre;

  end if;

return p_semestre;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
