CREATE OR REPLACE FUNCTION david.ft_avance_actividad ( ID_PERMISO_P bigint ) RETURNS bigint AS $body$
DECLARE

    TOTAL smallint;

BEGIN
    SELECT (CASE
                WHEN RTAP.CODIGO = 'PER_SOL' THEN 1
                WHEN RTAP.CODIGO = 'PER_AUT' THEN 2
                WHEN RTAP.CODIGO = 'PER_REC' THEN 0
                WHEN RTAP.CODIGO = 'REG_SAL' THEN 3
                WHEN RTAP.CODIGO = 'REG_ENT' THEN 4
                ELSE 0 END)
    INTO STRICT TOTAL
    FROM DAVID.RESIDENCIA_TIPO_ACT_PERMISO RTAP
             LEFT JOIN DAVID.RESIDENCIA_PERMISO RP
                       ON RTAP.ID_TIPO_ACT_PERMISO = RP.ID_TIPO_ACT_PERMISO
    WHERE RP.ID_PERMISO = ID_PERMISO_P;

    RETURN TOTAL;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
