CREATE OR REPLACE FUNCTION david.ft_calcular_ciclo (p_semestre text,p_id_persona integer,p_id_nivel_ensenanza integer) RETURNS bigint AS $body$
DECLARE

  p_ciclo bigint;

BEGIN

  select a.ciclo into STRICT p_ciclo
  from (SELECT distinct aca.id_persona, aca.ciclo, sum(distinct aca.credito) 
        from vw_acad_curso_alumno aca 
              where aca.id_persona=p_id_persona and aca.semestre=p_semestre and aca.id_tipo_condicion IN (1,2,7) 
              and aca.estado in ('1','0','M') and aca.Id_Nivel_Ensenanza=p_id_nivel_ensenanza 
        group by aca.id_persona, aca.ciclo
        order by 3 desc) a LIMIT 1;

  return p_ciclo;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
