CREATE OR REPLACE FUNCTION david.ft_calcular_creditos_mat_sem (p_id_semestre integer,p_id_persona integer,p_id_nivel_ensenanza integer) RETURNS bigint AS $body$
DECLARE

  p_creditos bigint;

BEGIN

  select sum(vac.credito) into STRICT p_creditos
  from acad_curso_alumno aca inner join vw_acad_carga_academica vac on vac.id_carga_curso=aca.id_carga_curso and vac.id_curso_detalle=aca.id_curso_detalle
        and aca.id_persona=p_id_persona and vac.id_semestre=p_id_semestre and aca.id_tipo_condicion<>3 
        and Vac.Id_Nivel_Ensenanza=p_id_nivel_ensenanza and Vac.Origen='O' and aca.estado='1';

  return p_creditos;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
