CREATE OR REPLACE FUNCTION david.ft_calcular_creditos_semestre (p_semestre text,p_id_persona integer,p_id_plan_programa integer) RETURNS bigint AS $body$
DECLARE

  p_creditos bigint;

BEGIN

  select sum(credito) into STRICT p_creditos 
  from Vw_Acad_Curso_Alumno where id_persona=p_id_persona and semestre=p_semestre 
  and id_plan_programa=p_id_plan_programa and id_tipo_condicion<>3 and estado<>'4';

  return p_creditos;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
