CREATE OR REPLACE FUNCTION david.ft_calcular_escuela_docentea (p_id_semestre integer,p_id_persona integer) RETURNS varchar AS $body$
DECLARE

  p_escuela varchar(4000);

BEGIN
  select nom_escuela into STRICT p_escuela
  from (
        SELECT Acc.Nom_Escuela,count(*)num 
        from acad_carga_curso_docente accd inner join vw_Acad_Carga_academica acc on Acc.Id_Carga_Curso=accd.id_carga_curso
        where id_semestre=p_id_semestre and origen='O'
        and id_persona=p_id_persona
        group by Acc.Nom_Escuela
        order by 2 desc) alias1 LIMIT 1;
  return p_escuela;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
