CREATE OR REPLACE FUNCTION david.ft_calcular_filial_docente (p_id_semestre integer,p_id_persona integer) RETURNS varchar AS $body$
DECLARE

  p_sede varchar(4000);

BEGIN
  select Distinct nombre into STRICT p_sede
  from (
        SELECT os.nombre,count(*)num 
        from acad_carga_curso_docente accd inner join vw_Acad_Carga_academica acc on Acc.Id_Carga_Curso=accd.id_carga_curso
        inner join ELISEO.Org_Sede os on os.Id_Sede=Acc.Id_Sede
        where id_semestre=p_id_semestre and origen='O'
        and id_persona=p_id_persona
        group by os.nombre
        order by 2 desc) alias1 LIMIT 1;
  return p_sede;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
