CREATE OR REPLACE FUNCTION david.ft_calcular_grupo_programa (p_semestre text,p_id_persona integer,p_id_plan_programa integer) RETURNS varchar AS $body$
DECLARE

  p_grupo varchar(500);
  p_ciclo bigint;

BEGIN

  select Ft_Calcular_Ciclo_Programa(p_semestre,p_id_persona,p_id_plan_programa) into STRICT p_ciclo;

  select a.grupo into STRICT p_grupo
  from (SELECT distinct aca.id_persona, CASE WHEN coalesce(aca.grupo::text, '') = '' THEN 'Único' WHEN aca.grupo='0' THEN 'Único'  ELSE aca.grupo END  grupo, sum(aca.credito)
        from vw_acad_curso_alumno aca 
              where aca.id_persona=p_id_persona and aca.semestre=p_semestre and aca.id_tipo_condicion<>3 and aca.id_plan_programa=p_id_plan_programa
              and aca.ciclo=p_ciclo and aca.estado<>'4'
        group by aca.id_persona, aca.grupo
        order by 3 desc) a LIMIT 1;

  return p_grupo;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
