CREATE OR REPLACE FUNCTION david.ft_calcular_llevado_ingles ( v_id_persona integer,v_id_curso integer ) RETURNS varchar AS $body$
DECLARE


	p_res	  varchar(800);
		

BEGIN

  select coalesce((
              select distinct 'N' from acad_curso_alumno aca inner join acad_curso_detalle acd on Acd.Id_Curso_Detalle=Aca.Id_Curso_Detalle
              left join acad_carga_curso ac on ac.id_carga_curso=aca.id_carga_curso
              left join acad_semestre_programa asp on Asp.Id_Semestre_Programa=ac.id_semestre_programa
              where Aca.id_persona=v_id_persona and acd.id_curso = v_id_curso 
                    and aca.id_tipo_condicion in (1,9,13,17) and aca.estado='1' ),'S') into STRICT p_res
;

  return p_res;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
