CREATE OR REPLACE FUNCTION david.ft_calcular_min_contrato (p_id_persona integer,p_id_programa_estudio integer) RETURNS varchar AS $body$
DECLARE

  p_semestre varchar(100);

BEGIN

  select min(Asm.Semestre) into STRICT p_semestre
  from acad_alumno_contrato aac
  inner join acad_semestre_programa asp on Asp.Id_Semestre_Programa=Aac.Id_Semestre_Programa
  inner join acad_semestre asm on Asm.Id_Semestre=Asp.Id_Semestre
  where aac.id_persona=p_id_persona and Asp.Id_Programa_Estudio=p_id_programa_estudio
  and aac.estado in ('1') and substr(Asm.Semestre,-2) in ('-1','-2');

  return p_semestre;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
