CREATE OR REPLACE FUNCTION david.ft_calcular_promedio_act (v_id_carga_curso bigint, v_id_persona bigint, v_id_programa_estudio bigint) RETURNS bigint AS $body$
DECLARE

    promedio_minimo_cat bigint;
    promedio_ponderado bigint;
    promedio_final bigint;

BEGIN

    if (v_id_programa_estudio = 1) then
        select round(round((sum(aae.nota*ace.ponderado)/coalesce(sum(ace.ponderado),1)),2),0) into STRICT promedio_ponderado
        from acad_alumno_evaluacion aae
        inner join acad_carga_evaluacion ace on ace.id_carga_evaluacion = aae.id_carga_evaluacion
        where aae.id_carga_curso = v_id_carga_curso and aae.id_persona = v_id_persona;

        select min(round((round((sum(coalesce(aae.nota,0)*ace.ponderado)/CASE WHEN sum(ace.ponderado)=0 THEN 1  ELSE sum(ace.ponderado) END )::numeric,2))::numeric,0)) into STRICT  promedio_minimo_cat
        from acad_carga_evaluacion ace 
        left join acad_alumno_evaluacion aae on ace.id_carga_evaluacion = aae.id_carga_evaluacion and aae.id_persona = v_id_persona
        left join acad_estrategia ae on ae.id_estrategia = ace.id_estrategia
        where ace.id_carga_curso = v_id_carga_curso
        group by ae.id_tipo_estrategia;

        if (promedio_minimo_cat < 12.5) then
            promedio_final := promedio_minimo_cat;
        else
            promedio_final := promedio_ponderado;
        end if;
    else
        select round(round((sum(aae.nota*ace.ponderado)/coalesce(sum(ace.ponderado),1)),2),0) into STRICT promedio_final
        from acad_alumno_evaluacion aae
        inner join acad_carga_evaluacion ace on ace.id_carga_evaluacion = aae.id_carga_evaluacion
        where aae.id_carga_curso = v_id_carga_curso and aae.id_persona = v_id_persona;
    end if;

    return promedio_final;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
