CREATE OR REPLACE FUNCTION david.ft_calcular_promedio_estrat (v_id_carga_curso bigint, v_id_persona bigint, v_id_tipo_estrategia bigint) RETURNS bigint AS $body$
DECLARE

    promedio_final bigint;

BEGIN

    select round((sum(coalesce(aae.nota,0)*ace.ponderado)/sum(ace.ponderado))::numeric,2) into STRICT  promedio_final
    from acad_carga_evaluacion ace 
    left join acad_alumno_evaluacion aae on ace.id_carga_evaluacion = aae.id_carga_evaluacion and aae.id_persona = v_id_persona
    left join acad_estrategia ae on ae.id_estrategia = ace.id_estrategia
    where ace.id_carga_curso = v_id_carga_curso
    and ae.id_tipo_estrategia=v_id_tipo_estrategia;

    return promedio_final;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
