CREATE OR REPLACE FUNCTION david.ft_calc_dia_mat (P_ID_SEMESTRE text, P_FECHA timestamp(0)) RETURNS varchar AS $body$
DECLARE

DIA_CONTRATO varchar(3):=0;

BEGIN

    SELECT  LPAD(NUMERO,2,0) INTO STRICT DIA_CONTRATO
    FROM (SELECT row_number() OVER () AS NUMERO, A.FECHA 
            FROM (select TO_CHAR(FECHA_registro,'YYYYMMDD') FECHA
                    from vw_acad_alumno_contrato where id_semestre=P_ID_SEMESTRE and estado='1'
                    GROUP BY TO_CHAR(FECHA_registro,'YYYYMMDD') 
                    ORDER BY 1 ASC) A ) X
    WHERE X.FECHA=TO_CHAR(P_FECHA,'YYYYMMDD');

    RETURN DIA_CONTRATO;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
