CREATE OR REPLACE FUNCTION david.ft_cantidad_cursos_repetidos (v_id_persona integer, v_id_plan_programa integer, v_id_semestre integer, v_tipo text) RETURNS bigint AS $body$
DECLARE

  p_res	  bigint;		


BEGIN
    
    if (v_tipo='CN') then
    
      select sum(CASE WHEN ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle)='S' THEN 0  ELSE credito END ) into STRICT p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4';

    elsif (v_tipo='SN') then
    
      select sum(CASE WHEN ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle)='S' THEN 0  ELSE 1 END )  into STRICT p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4';

    elsif (v_tipo='CR') then
    
      select sum(CASE WHEN ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle)='S' THEN credito  ELSE 0 END ) into STRICT p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4';

    elsif (v_tipo='SR') then
    
      select sum(CASE WHEN ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle)='S' THEN 1  ELSE 0 END )  into STRICT p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4';

    elsif (v_tipo='SR2V') then
    
      select sum(CASE WHEN ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle)='S' THEN 1  ELSE 0 END )  into STRICT p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4'
        and ft_cant_cur_veces_repetidos(id_persona, id_plan_curso)=2;

    elsif (v_tipo='SR3V') then
    
      select sum(CASE WHEN ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle)='S' THEN 1  ELSE 0 END )  into STRICT p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4'
        and ft_cant_cur_veces_repetidos(id_persona, id_plan_curso)=3;

        
    end if;


  return p_res;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
