CREATE OR REPLACE FUNCTION david.ft_cant_cur_veces_repetidos (v_id_persona integer, v_id_plan_curso integer) RETURNS bigint AS $body$
DECLARE

  p_id_area	  bigint;	
  p_res	  bigint;		


BEGIN

  select coalesce((select count(*) 
  from vw_acad_curso_alumno aca
  where id_plan_curso=v_id_plan_curso and id_tipo_condicion=2),0) into STRICT p_res
;


  return p_res;

END;
