CREATE OR REPLACE FUNCTION david.ft_cant_horas_curso_docente (v_id_persona integer,v_id_carga_curso integer) RETURNS bigint AS $body$
DECLARE

  p_res	  bigint;		
  p_nivel_ensenaza	  bigint;
  p_id_escuela	  bigint;


BEGIN

  select coalesce((select sum(horas_mes*CASE WHEN accd.factor=0 THEN 1  ELSE accd.factor END )/16
              from tb_pcd_horas_mes_ccd thm inner join acad_carga_curso_docente accd on accd.id_carga_curso_docente=thm.id_carga_curso_docente 
              where accd.Id_Carga_Curso=v_id_carga_curso and accd.id_persona=v_id_persona)
          ,0)
  into STRICT p_res 
;
  
  return p_res;

END;
$body$
