CREATE OR REPLACE FUNCTION david.ft_cant_horas_mes_ccd (v_id_persona integer,v_id_comision integer,v_mes text,v_tipo text) RETURNS bigint AS $body$
DECLARE

  p_res	  bigint;		
  p_nivel_ensenaza	  bigint;
  p_id_escuela	  bigint;


BEGIN



  if (v_tipo='CM') then
    select coalesce((select horas_laborales
                from acad_persona_comision apcc inner join Acad_Persona_Contrato apc on Apc.Id_Persona_Contrato=Apcc.Id_Persona_Contrato
                where apcc.Id_Persona_Comision=v_id_comision and (v_mes)::numeric  Between (to_char(apc.fecha_inicio,'MM'))::numeric  and (to_char(apc.fecha_fin,'MM'))::numeric  )
            ,0)
    into STRICT p_res 
;
  elsif (v_tipo='CR') then

    select Aca.Id_Nivel_Ensenanza,Aca.Id_Escuela into STRICT p_nivel_ensenaza,p_id_escuela
    from vw_acad_carga_academica aca where Aca.Origen='O'
    and Aca.Id_Carga_Curso=v_id_comision;

    if (p_nivel_ensenaza=2 or p_id_escuela=83) then
    
      select coalesce((select sum(horas_mes*CASE WHEN accd.factor=0 THEN 1  ELSE accd.factor END )/4 
                  from tb_pcd_horas_mes_ccd thm inner join acad_carga_curso_docente accd on accd.id_carga_curso_docente=thm.id_carga_curso_docente 
                  where accd.Id_Carga_Curso=v_id_comision and accd.id_persona=v_id_persona and mes=v_mes)
              ,0)
      into STRICT p_res 
;

    else
    
      select coalesce((select max(ft_num_horas_curso_doc( accd.Id_Carga_Curso_Docente )*CASE WHEN accd.factor=0 THEN 1  ELSE accd.factor END )
                  from acad_carga_curso acc inner join Acad_Carga_Curso_Docente accd on accd.id_carga_curso=acc.id_carga_curso
                  inner join Acad_Modulo_Detalle amd on Amd.Id_Modulo_Detalle=Accd.Id_Modulo_Detalle 
                  where accd.Id_Carga_Curso=v_id_comision and accd.id_persona=v_id_persona 
                  and (v_mes)::numeric  Between (to_char(amd.fecha_inicio,'MM'))::numeric  and (to_char(amd.fecha_fin,'MM'))::numeric  )
              ,0)
      into STRICT p_res 
;

    end if;

  end if;

  return p_res;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
