CREATE OR REPLACE FUNCTION david.ft_cant_horas_mes_cd (v_id_comision integer,v_mes text,v_tipo text) RETURNS bigint AS $body$
DECLARE

  p_res	  bigint;		
  p_nivel_ensenaza	  bigint;
  p_id_escuela	  bigint;


BEGIN



  if (v_tipo='CM') then
    select coalesce((select horas_laborales
                from acad_persona_comision apcc inner join Acad_Persona_Contrato apc on Apc.Id_Persona_Contrato=Apcc.Id_Persona_Contrato
                where apcc.Id_Persona_Comision=v_id_comision and (v_mes)::numeric  Between (to_char(apc.fecha_inicio,'MM'))::numeric  and (to_char(apc.fecha_fin,'MM'))::numeric  )
            ,0)
    into STRICT p_res 
;
  elsif (v_tipo='CR') then

    select Aca.Id_Nivel_Ensenanza,Aca.Id_Escuela into STRICT p_nivel_ensenaza,p_id_escuela
    from vw_acad_carga_academica aca inner join Acad_Carga_Curso_Docente accd on Accd.Id_Carga_Curso=Aca.Id_Carga_Curso and Aca.Origen='O'
    where Accd.Id_Carga_Curso_Docente=v_id_comision;

    if (p_nivel_ensenaza=2 or p_id_escuela=83) then
    
      select coalesce((select horas_mes/4 from tb_pcd_horas_mes_ccd where Id_Carga_Curso_Docente=v_id_comision and mes=v_mes)
              ,0)
      into STRICT p_res 
;

    else
    
      select coalesce((select ft_num_horas_curso_doc( accd.Id_Carga_Curso_Docente )
                  from Acad_Carga_Curso_Docente accd inner join Acad_Modulo_Detalle amd on Amd.Id_Modulo_Detalle=Accd.Id_Modulo_Detalle 
                  where accd.Id_Carga_Curso_Docente=v_id_comision and (v_mes)::numeric  Between (to_char(amd.fecha_inicio,'MM'))::numeric  and (to_char(amd.fecha_fin,'MM'))::numeric  )
              ,0)
      into STRICT p_res 
;

    end if;

  end if;

  return p_res;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
