CREATE OR REPLACE FUNCTION david.ft_carreras_persona (v_id_persona integer) RETURNS varchar AS $body$
DECLARE

  p_escuela varchar(4000);

BEGIN
  select wm_concat(p_escuela) into STRICT p_escuela
  from (SELECT Distinct nombre_escuela p_escuela from Vw_Acad_Alumno_Contrato 
         where id_persona=v_id_persona and estado='1' and Id_Tipo_Contrato in (1,2,4,5) ) alias2;
  return p_escuela;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
