CREATE OR REPLACE FUNCTION david.ft_ciclo_curso_persona ( v_id_persona integer,v_id_curso_detalle integer ) RETURNS varchar AS $body$
DECLARE


	p_ciclo	  varchar(800);
		

BEGIN

  select max(ciclo) into STRICT p_ciclo
  from acad_plan_curso apc inner join acad_plan_programa app on app.id_plan=Apc.Id_Plan 
  inner join vw_curso_equivalente vce on Vce.Curso_Equiv=apc.id_curso_detalle and Vce.id_curso_detalle=v_id_curso_detalle
  inner join acad_alumno_plan aap on Aap.Id_Plan_Programa=App.Id_Plan_Programa and aap.id_persona=v_id_persona;
	/*select max(ciclo) into p_ciclo
from acad_plan_curso apc inner join acad_plan_programa app on app.id_plan=Apc.Id_Plan and apc.id_curso_detalle=v_id_curso_detalle
inner join acad_alumno_plan aap on Aap.Id_Plan_Programa=App.Id_Plan_Programa and aap.id_persona=v_id_persona;*/
return p_ciclo;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
