CREATE OR REPLACE FUNCTION david.ft_ciclo_plan_alumno_last (p_id_persona integer,p_id_plan_programa integer) RETURNS bigint AS $body$
DECLARE


  p_creditos integer;
  p_total_creditos integer;
  p_total_ciclos integer;
  p_res_ciclo  integer;

  CredCiclo CURSOR FOR
  SELECT Apc.Ciclo,sum(acd.credito) creditos from acad_plan_programa app inner join acad_plan_curso apc on apc.id_plan=app.id_plan
  inner join acad_curso_detalle acd on acd.id_curso_detalle=apc.id_curso_detalle
  where App.Id_Plan_Programa=p_id_plan_programa and coalesce(Apc.Id_Plan_Curso_Electivo::text, '') = ''
  group by Apc.Ciclo
  order by Apc.Ciclo;

BEGIN

  p_total_creditos:=0;
  p_total_ciclos:=0;

  select coalesce((select sum(acp.credito)
                from VW_PLAN_PROGRAMA vpp inner join VW_ACAD_CURSO_PLAN acp on VPP.ID_PLAN=ACP.ID_PLAN
                left join(select aca.Id_Plan_Programa,aca.promedio,aca.id_tipo_condicion,
                            CASE WHEN coalesce(apc.Id_Plan_Curso_Electivo::text, '') = '' THEN apc.Id_Plan_Curso  ELSE apc.Id_Plan_Curso_Electivo END  Id_Plan_Curso
                            from Acad_Curso_Alumno aca inner join  acad_plan_curso apc on Apc.Id_Plan_Curso=Aca.Id_Plan_Curso
                            where aca.id_persona=p_id_persona  and aca.estado='1' and aca.Id_Tipo_Condicion not in (3,4,5,7,6,15) ) aca
                            on aca.Id_Plan_Programa=Vpp.Id_Plan_Programa and aca.Id_Plan_Curso=Acp.Id_Plan_Curso
                where vpp.id_plan_programa=p_id_plan_programa and coalesce(acp.ID_PLAN_CURSO_ELECTIVO::text, '') = ''
                and acp.estado='1' ),0) into STRICT p_creditos
;

  FOR N in CredCiclo LOOP
  p_total_ciclos:=p_total_ciclos+1;
  END LOOP;

  FOR cc in CredCiclo LOOP
    
    p_total_creditos:=p_total_creditos+cc.creditos;

    if (cc.ciclo=p_total_ciclos) then    
        if (p_creditos>=p_total_creditos) then        
          p_res_ciclo:=cc.ciclo;
        else          
          p_res_ciclo:=p_res_ciclo;
        end if;

    else
      
      if (p_creditos<p_total_creditos) then 
        p_res_ciclo:=cc.ciclo;
        EXIT;
      else
        p_res_ciclo:=p_res_ciclo+1;
      end if;

    end if;

  end loop;

  return p_res_ciclo;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
