CREATE OR REPLACE FUNCTION david.ft_creditos_acumulados_plan_s (p_id_persona integer,p_id_plan_programa integer,p_id_semestre integer) RETURNS bigint AS $body$
DECLARE

  p_plan bigint;
  p_creditos bigint;
  p_semestre varchar(400);
  p_semestre_ant varchar(400);

BEGIN

  select semestre,Semestre_Ant into STRICT p_semestre,p_semestre_ant from acad_semestre where id_semestre=p_id_semestre;

  select coalesce((select sum(coalesce(Acpe.Credito,Acp.Credito))
  from Acad_Curso_Alumno aca inner join Vw_Acad_Curso_Plan acp on Acp.Id_Plan_Curso=Aca.Id_Plan_Curso
  left join Vw_Acad_Curso_Plan acpe on Acpe.Id_Plan_Curso=Acp.Id_Plan_Curso_Electivo
  left join acad_carga_curso acc on acc.id_carga_curso=aca.id_carga_curso
  left join acad_semestre_programa asp on Asp.Id_Semestre_Programa=Acc.Id_Semestre_Programa
  left join acad_semestre asm on asm.id_semestre=Asp.Id_Semestre
  where aca.id_persona=p_id_persona and aca.Id_Plan_Programa=p_id_plan_programa
  and aca.Id_Tipo_Condicion in (1,13) and aca.estado='1'
  and replace(coalesce(asm.semestre,p_semestre_ant),'M','2')<replace(p_semestre,'M','2') ),0) into STRICT p_creditos
;
  return p_creditos;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
