CREATE OR REPLACE FUNCTION david.ft_creditos_ap_semestre_alumno (v_id_persona integer, v_id_escuela integer, v_id_semestre integer) RETURNS bigint AS $body$
DECLARE

  p_creditos	  bigint;		


BEGIN
  select coalesce((select sum(credito)
  from Vw_Acad_Curso_Alumno 
  where id_persona=v_id_persona 
  and id_escuela=v_id_escuela 
  and id_semestre=v_id_semestre
  and estado<>'4' and id_tipo_condicion='1'),0) into STRICT p_creditos
;

  return p_creditos;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
