CREATE OR REPLACE FUNCTION david.ft_cursos_semestre_alumno (v_id_persona integer, v_id_plan_programa integer, v_id_semestre integer) RETURNS bigint AS $body$
DECLARE

  p_cursos	  bigint;		


BEGIN
  select count(*) into STRICT p_cursos
  from Vw_Acad_Curso_Alumno 
  where id_persona=v_id_persona 
  and id_plan_programa=v_id_plan_programa 
  and id_semestre=v_id_semestre
  and estado<>'4';

  return p_cursos;

END;
$body$
