CREATE OR REPLACE FUNCTION david.ft_curso_aprobado_nom (V_ID_PERSONA integer,V_NOMBRE text) RETURNS varchar AS $body$
DECLARE

P_RESULTADO varchar(500);

BEGIN

  SELECT DISTINCT 'S' INTO STRICT P_RESULTADO
  FROM VW_ACAD_CURSO_ALUMNO WHERE ID_PERSONA=V_ID_PERSONA AND NOMBRE_CURSO=V_NOMBRE AND ID_TIPO_CONDICION=1;

  return  P_RESULTADO;

END;
