CREATE OR REPLACE FUNCTION david.ft_deuda_alumno_dpto (v_id_persona integer, v_anio integer, v_depto text) RETURNS varchar AS $body$
DECLARE

  deuda	  varchar(500);		


BEGIN

DECLARE

  --v_codigo varchar(100) := '';
  
  BEGIN


    --select codigo into v_codigo from moises.persona where id_persona=v_id_persona;
    SELECT CASE WHEN LENGTH(SIGN(coalesce(SUM(TOTAL),0)))=2 THEN ''  ELSE 0 END ||SIGN(coalesce(SUM(TOTAL),0))||'-'||coalesce(ABS(SUM(TOTAL)),0) into STRICT deuda
    FROM (
      SELECT TOTAL
      FROM eliseo.VW_SALES_MOV
      WHERE ID_ENTIDAD = 7124 AND ID_DEPTO in (v_depto) AND ID_ANHO = v_anio AND ID_CLIENTE = v_id_persona AND ID_TIPOVENTA IN (1,2,3) 
      
UNION ALL

      SELECT SUM(IMPORTE)*CASE WHEN SIGN(SUM(IMPORTE))=1 THEN -1  ELSE 0 END  AS TOTAL
      FROM eliseo.VW_SALES_ADVANCES
      WHERE ID_ENTIDAD = 7124 AND ID_DEPTO in (v_depto) AND ID_ANHO = v_anio AND ID_CLIENTE = v_id_persona
      /*UNION ALL
      SELECT TOTAL 
      FROM ( 
        SELECT   ID_PERSONAL,   SUM(IMPORTE) TOTAL 
        FROM ARON.MAT_LOCK_FILIAL@DBL_NOE 
        WHERE ID_VENTA = '001-'||v_anio 
        AND ID_PERSONAL = v_codigo 
        GROUP BY ID_PERSONAL                         
        UNION 
        SELECT   ID_PERSONAL,   SUM(IMPORTE) TOTAL 
        FROM ARON.MAT_LOCK_TARA@DBL_NOE 
        WHERE ID_VENTA = '001-'||v_anio 
        AND ID_PERSONAL = v_codigo 
        GROUP BY ID_PERSONAL                         
      )*/
 
  
    ) alias16;

  return deuda;

  END;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
