CREATE OR REPLACE FUNCTION david.ft_deuda_alumno_n (v_id_persona integer, v_anio integer) RETURNS varchar AS $body$
DECLARE

  deuda	  varchar(500);		


BEGIN

DECLARE
  v_codigo varchar(100) := '';

  BEGIN


    select codigo into STRICT v_codigo from moises.persona where id_persona=v_id_persona;

    SELECT CASE WHEN CASE WHEN LENGTH(SIGN(coalesce(SUM(TOTAL),0)))=2 THEN ''  ELSE 0 END ||SIGN(coalesce(SUM(TOTAL),0))='-1' THEN 'a Favor : ' WHEN CASE WHEN LENGTH(SIGN(coalesce(SUM(TOTAL),0)))=2 THEN ''  ELSE 0 END ||SIGN(coalesce(SUM(TOTAL),0))='01' THEN 'Debe : '  ELSE '' END
    ||coalesce(ABS(SUM(TOTAL)),0) into STRICT deuda
    FROM (
      SELECT TOTAL
      FROM eliseo.VW_SALES_MOV
      WHERE ID_ENTIDAD = 7124 AND ID_DEPTO = '1' AND ID_ANHO = v_anio AND ID_CLIENTE = v_id_persona AND ID_TIPOVENTA IN (1,2,3) 
      
UNION ALL

      SELECT SUM(IMPORTE)*CASE WHEN SIGN(SUM(IMPORTE))=1 THEN -1  ELSE 0 END  AS TOTAL
      FROM eliseo.VW_SALES_ADVANCES
      WHERE ID_ENTIDAD = 7124 AND ID_DEPTO = '1' AND ID_ANHO = v_anio AND ID_CLIENTE = v_id_persona  
    ) alias21;

  return deuda;

  END;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
