CREATE OR REPLACE FUNCTION david.ft_div_4_carga_tema (p_id_carga_curso integer) RETURNS integer AS $body$
DECLARE

  v_res integer;

BEGIN
  
    select count(*)/4 into STRICT v_res
    from Acad_Carga_Evaluacion
    where id_carga_curso=p_id_carga_curso
    and ponderado<>'0';

    return v_res;
  end;
$body$
