CREATE OR REPLACE FUNCTION david.ft_enoc_cargo (P_ID_PERSONA integer) RETURNS varchar AS $body$
DECLARE

  p_resultado varchar(500);

BEGIN

  SELECT coalesce((SELECT MIN(UPPER(P.NOMBRE))
      FROM ENOC.Vw_Trabajador T INNER JOIN ENOC.PLLA_PUESTO P ON P.ID_PUESTO=T.ID_PUESTO 
      WHERE ID_PERSONA=P_ID_PERSONA AND T.ID_ENTIDAD='7124'),'DOCENTE') INTO STRICT p_resultado
;

  RETURN p_resultado;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
