CREATE OR REPLACE FUNCTION david.ft_enoc_fecha_ingreso (P_ID_PERSONA integer) RETURNS varchar AS $body$
DECLARE

  p_resultado timestamp(0);

BEGIN

      SELECT MIN(T.Fecha_Ingreso) INTO STRICT p_resultado
      FROM ENOC.Vw_Trabajador T INNER JOIN ENOC.PLLA_PUESTO P ON P.ID_PUESTO=T.ID_PUESTO
      WHERE ID_PERSONA=P_ID_PERSONA AND T.ID_ENTIDAD='7124';

  RETURN p_resultado;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
