CREATE OR REPLACE FUNCTION david.ft_escuela_anterior (p_id_persona integer,p_id_escuela_act integer) RETURNS bigint AS $body$
DECLARE


p_id_escuela bigint;


BEGIN

  select Distinct id_escuela into STRICT p_id_escuela
  from (
        SELECT id_escuela from Vw_Alumno_Plan_Programa where id_persona=p_id_persona and id_escuela<>p_id_escuela_act and Id_Nivel_Ensenanza in (1,2)
        order by Fecha_Registro desc) alias1 LIMIT 1;

  return p_id_escuela;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
