CREATE OR REPLACE FUNCTION david.ft_estudiante_cred_aprob (v_codigo text) RETURNS bigint AS $body$
DECLARE

  p_creditos	  bigint;	
  p_id_persona integer;
  p_id_plan_programa integer;


BEGIN

  select id_persona into STRICT p_id_persona from moises.persona_natural_alumno where codigo=v_codigo;

  select id_plan_programa into STRICT p_id_plan_programa 
  from (SELECT id_plan_programa from Vw_Alumno_Plan_Programa 
        where id_persona=p_id_persona and Id_Nivel_Ensenanza=1
        order by estado desc,Fecha_Registro) alias0 LIMIT 1;

  select coalesce((select sum(Acd.Credito)
              from acad_curso_alumno aca inner join acad_plan_curso apc on Apc.Id_Plan_Curso=Aca.Id_Plan_Curso
              left join acad_plan_curso apce on Apce.Id_Plan_Curso=Apc.Id_Plan_Curso_Electivo
              inner join acad_curso_detalle acd on Acd.Id_Curso_Detalle=CASE WHEN coalesce(Apce.Id_Plan_Curso::text, '') = '' THEN apc.id_curso_detalle  ELSE apce.id_curso_detalle END
              where aca.id_persona=p_id_persona and aca.id_plan_programa=p_id_plan_programa and aca.Id_Tipo_Condicion in (1,13,17) ),0)  into STRICT p_creditos
;

  return p_creditos;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
