CREATE OR REPLACE FUNCTION david.ft_evidencia_aprendizaje ( p_id_carga_unidad bigint ) RETURNS varchar AS $body$
DECLARE

    r_descripcion varchar(4000);

BEGIN

SELECT
acure.DETALLE INTO STRICT r_descripcion
FROM 
ACAD_CARGA_UNID_RA_EV acure 
INNER JOIN ACAD_CARGA_UNIDAD_RA accra ON acure.ID_CARGA_UNIDAD_RA =accra.ID_CARGA_UNIDAD_RA 
WHERE accra.ID_CARGA_UNIDAD  = p_id_carga_unidad  LIMIT 1;

    RETURN r_descripcion;
END;
$body$
