CREATE OR REPLACE FUNCTION david.ft_facultad_alumno (P_ID_PERSONA integer) RETURNS varchar AS $body$
DECLARE

  P_FACULTAD varchar(500);

BEGIN
    /*SELECT NOM_FACULTAD INTO P_FACULTAD
    FROM (SELECT AAP.NOM_FACULTAD FROM VW_ALUMNO_PLAN_PROGRAMA AAP
          WHERE AAP.ID_PERSONA=P_ID_PERSONA AND AAP.ESTADO=1
          ORDER BY AAP.ID_NIVEL_ENSENANZA,AAP.NOMBRE_PLAN DESC)
    WHERE ROWNUM=1;
    
    */
SELECT Distinct NOM_FACULTAD INTO STRICT P_FACULTAD
    FROM DAVID.VW_ALUMNO_PLAN_PROGRAMA VPP
    WHERE  ID_PERSONA=P_ID_PERSONA
    AND ID_PLAN_PROGRAMA = (SELECT  ID_PLAN_PROGRAMA
                            FROM (SELECT ID_PLAN_PROGRAMA,ID_PERSONA FROM DAVID.VW_ALUMNO_PLAN_PROGRAMA WHERE ESTADO=1 ORDER BY LPAD(CASE WHEN ID_NIVEL_ENSENANZA=1 THEN 9 WHEN ID_NIVEL_ENSENANZA=2 THEN 9 WHEN ID_NIVEL_ENSENANZA=3 THEN 9 WHEN ID_NIVEL_ENSENANZA=4 THEN 9  ELSE 1 END ,2,'0')||NOMBRE_PLAN DESC ) alias1 
                            WHERE ID_PERSONA=VPP.ID_PERSONA  LIMIT 1);
    RETURN P_FACULTAD;


END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
