CREATE OR REPLACE FUNCTION david.ft_fecha_record ( p_id_curso_detalle integer, p_id_plan_curso integer, p_id_plan integer, p_id_tipo_condicion integer, p_id_persona integer, p_promedio bigint ) RETURNS timestamp(0) AS $body$
DECLARE

  v_fecha timestamp(0);

BEGIN
	SELECT fecha_registro INTO STRICT v_fecha
  FROM (SELECT coalesce(acd.fecha_fin,aca.fecha_registro) fecha_registro from acad_curso_alumno aca 
        left join(select accd.id_carga_curso,max(Amd.Fecha_Fin) fecha_fin 
                   from acad_carga_curso_docente accd inner join acad_modulo_detalle amd 
                        on Amd.Id_Modulo_Detalle=Accd.Id_Modulo_Detalle
                   group by accd.id_carga_curso) acd on acd.id_carga_curso=aca.id_carga_curso 
        where aca.id_plan_curso=p_id_plan_curso and aca.id_persona=p_id_persona
        and aca.promedio=p_promedio and Aca.Id_Tipo_Condicion=p_id_tipo_condicion
        order by aca.id_carga_curso
  ) alias3
  WHERE (fecha_registro IS NOT NULL AND fecha_registro::text <> '')   LIMIT 1;

      return v_fecha;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
